const items = $input.all();
const results = [];

for (const item of items) {
  const data = item.json;

  if (data.automated) {
    results.push({ json: data });
    continue;
  }

  const chatId = data.message?.chat?.id || data.chatId;
  const userId = data.message?.from?.id || data.userId;
  const username = data.message?.from?.username || data.username || 'Usuario';
  const messageText = (data.message?.text || '').trim();

  let action = 'unknown';
  let payload = {};

  // Comandos prioritarios (siempre se procesan)
  if (messageText === '/start') {
    action = 'start_evaluation';
    payload = {
      chatId,
      userId,
      username,
      action,
      automated: false,
      message: `Â¡Bienvenido ${username}! ðŸ‘‹\n\nVoy a hacerte 5 preguntas sobre tu desempeÃ±o.\nPor favor responde con un nÃºmero del 1 al 5.\n\nðŸ“ Pregunta 1/5:\nÂ¿QuÃ© tan satisfecho estÃ¡s con tu nivel de productividad? (1=Muy insatisfecho, 5=Muy satisfecho)`
    };
  } else if (messageText === '/resultados') {
    action = 'show_results';
    payload = { chatId, userId, username, action };
  } else if (/^[1-5]$/.test(messageText)) {
    // Respuesta numÃ©rica 1-5
    action = 'save_answer';
    payload = {
      chatId,
      userId,
      username,
      action,
      answer: parseInt(messageText),
      timestamp: new Date().toISOString(),
      needsSessionCheck: true  // âœ… Marcar para validaciÃ³n posterior
    };
  } else {
    // âœ… NUEVO: Para cualquier otro mensaje, enviar a validaciÃ³n de sesiÃ³n
    // Si tiene sesiÃ³n activa â†’ error "responde 1-5"
    // Si NO tiene sesiÃ³n â†’ help normal
    action = 'check_session_for_invalid';
    payload = {
      chatId,
      userId,
      username,
      action,
      originalMessage: messageText
    };
  }

  results.push({ json: payload });
}

return results;

