const items = $input.all();
const results = items.map(i => i.json.documents).flat();

if (results.length === 0) {
  return {
    json: {
      status: 'success',
      timestamp: new Date().toISOString(),
      summary: {
        totalEvaluations: 0,
        message: 'No hay evaluaciones completadas'
      },
      evaluations: []
    }
  };
}

const totalEvaluations = results.length;
const avgTotalScore = results.reduce((sum, r) => sum + r.metrics.totalScore, 0) / totalEvaluations;
const avgPercentage = results.reduce((sum, r) => sum + r.metrics.percentage, 0) / totalEvaluations;

const levelDistribution = {};
results.forEach(r => {
  const level = r.metrics.performanceLevel;
  levelDistribution[level] = (levelDistribution[level] || 0) + 1;
});

const categoryAverages = {};
const categoryNames = ['Productividad', 'ComunicaciÃ³n de Equipo', 'Cumplimiento de Objetivos', 'Oportunidades de Crecimiento', 'Balance Trabajo-Vida'];

categoryNames.forEach(cat => {
  const scores = results.map(r => r.categoryScores[cat]).filter(s => s !== undefined);
  categoryAverages[cat] = scores.length > 0 
    ? (scores.reduce((sum, s) => sum + s, 0) / scores.length).toFixed(2)
    : 0;
});

const sortedByScore = [...results].sort((a, b) => b.metrics.totalScore - a.metrics.totalScore);
const topPerformers = sortedByScore.slice(0, 5).map(r => ({
  username: r.username,
  score: r.metrics.totalScore,
  percentage: r.metrics.percentage,
  level: r.metrics.performanceLevel
}));

const bottomPerformers = sortedByScore.slice(-5).reverse().map(r => ({
  username: r.username,
  score: r.metrics.totalScore,
  percentage: r.metrics.percentage,
  level: r.metrics.performanceLevel
}));

return {
  json: {
    status: 'success',
    timestamp: new Date().toISOString(),
    summary: {
